import { Component } from '@angular/core';
import { FormBuilder, FormGroup, FormsModule, ReactiveFormsModule, Validators } from '@angular/forms';
import { Reponse } from '../../model/reponse';
import { Question } from '../../model/question';
import { QuestionServiceService } from '../../service/question-service.service';
import { Router, RouterOutlet } from '@angular/router';
import { Vote } from '../../model/vote';
import { HttpClientModule } from '@angular/common/http';
import { BrowserModule } from '@angular/platform-browser';
import { CommonModule } from '@angular/common';
import {MatIconModule} from '@angular/material/icon';
import { Technologie } from '../../model/technologie';


@Component({
  selector: 'app-forum',
  templateUrl: './forum.component.html',
  styleUrls: ['./forum.component.css']
})
export class ForumComponent {
  currentUser!: String;
  isReady:boolean=false;
  questform!:FormGroup;
  repform!: FormGroup;
  listofrep: Reponse[] = [];
  listofQuestion: Question[] = [];
  tech=Technologie
  nbrlike:Number[]=[]
  constructor(private ps:QuestionServiceService,private formBuilder: FormBuilder,private route:Router) { }

  ngOnInit(): void {
    this.currentUser = "65d621813ece05768b528f25";
    this.getallQuestion();
    this.initForm();
    this.initFormquest()
  }
  addquestion(){
    this.ps.ajoutQuestion(this.questform.value,"65d621813ece05768b528f25").subscribe(
      res=>{
          this.getallQuestion()
      }
    )
  }
  initFormquest() {
    this.questform = this.formBuilder.group({
      contenue: ['', Validators.required],
      tech:[[], Validators.required],

  });

  this.questform.valueChanges.subscribe(
    data=>{console.log(this.questform.value)}
  )
}


  getallQuestion(){
    this.ps.getQuestions().subscribe(
      data=>{
        console.log(data)
        this.listofQuestion=data;
        this.isReady=true;
        for(let i of this.listofQuestion){
          let index=this.listofQuestion.indexOf(i);
          this.ps.getnbrvote(this.listofQuestion[index].id).subscribe(
            res=>{

              this.nbrlike[index]=res;
            }
          )
        }
      }
    )
  }
  ajouterreponse(post:String){
    console.log(this.repform.value);
    this.ps.addreponse(post,"65d621813ece05768b528f25",this.repform.value).subscribe(
    data=>{
      this.getallQuestion();
        }

    );

  }
  getcmtbypos(post:any){
    this.ps.getreponsebyquestion(post).subscribe(
      data=>{

      }
    )
  }
  initForm() {
    this.repform = this.formBuilder.group({
      contenue: ['', Validators.required],

  });

  this.repform.valueChanges.subscribe(
    data=>{console.log(this.repform)}
  )
}
addlike(post:Question){
  this.ps.vote(post.id,"65d621813ece05768b528f25",post).subscribe(
    data=>{
        console.log(data)
        this.ps.getnbrvote(post.id).subscribe(
          res=>{
            console.log(this.nbrlike)
            let index=this.listofQuestion.indexOf(post);
            console.log(index)
            this.nbrlike[index]=res;
          }
        )
      }
  )
}
// onTechnologySelected(technology: Technologie) {
//   // Handle the selected technology here
//   this.questform.patchValue({
//     tech: technology
//   });
// }
// onTechnologySelected(select: HTMLSelectElement) {
//   const selectedOptions = Array.from(select.selectedOptions).map(option => option.value);
//   this.questform.get('tech')?.setValue(selectedOptions);
// }
delete(cmt:Reponse){
  this.ps.deletereponse(cmt.id).subscribe(
    res=>{
      this.getallQuestion();
    }
  )
}
modifier(rep:Reponse){
  this.ps.updateReponse(rep.id,rep).subscribe(
    data=>{
      this.getallQuestion();
    }
  )
}
}
<link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/3.4.1/css/bootstrap.min.css">
<script src="https://ajax.googleapis.com/ajax/libs/jquery/3.7.1/jquery.min.js"></script>
<script src="https://cdn.jsdelivr.net/npm/bootstrap@5.3.2/dist/js/bootstrap.bundle.min.js"></script>
<link rel="stylesheet" href="https://fonts.googleapis.com/css2?family=Material+Symbols+Outlined:opsz,wght,FILL,GRAD@20..48,100..700,0..1,-50..200" />
<style>
  .material-symbols-outlined {
    font-variation-settings:
    'FILL' 0,
    'wght' 400,
    'GRAD' 0,
    'opsz' 24;
    color: #ADD8E6;
  }
  </style>
  <div class="container">
    <div class="post">
      <form [formGroup]="questform">
        <div>
          <div class="form-group"  >
            <label for="comment">Question:</label>
            <textarea class="form-control" rows="5" id="comment" formControlName="contenue"></textarea>
            <div class="container mt-3">
              <h2>Data List</h2>
              <label for="browser" class="form-label">Choose your browsers from the list:</label>
              <select class="form-control" multiple name="browser" id="browser" formControlName="tech">
                <option *ngFor="let c of tech | keyvalue" [value]="c.key">{{ c.value }}</option>
              </select>              
            </div>

            <!-- <app-autocomplete ngSkipHydration (technologySelected)="onTechnologySelected($event)"></app-autocomplete> -->
          </div>
          <div class="col-6 text-right"> 
            <button type="button" class="btn btn-primary btn-sm" (click)="addquestion()" >Poser Questopn</button>
          </div>
        </div>
      </form>
    </div>
  </div>

<div class="container">

  <div *ngFor="let item of listofQuestion;let idx = index"  class="post">
    <p>{{item.contenue}}</p>

    <div *ngFor="let h of item.hashtag">
      <a style="color: blue;font-weight: bold;" href="#">#{{h.technologie}}</a>
    </div>

      <p class="author">Posted by:{{ item?.user?.firstName}}</p>

    <span class="material-symbols-outlined clickable" (click)="addlike(item)">
      shift_lock
      </span>
      <a>{{nbrlike[idx]}}</a>

    <div class="comments" *ngFor="let cmt of item.reponses">
      <div class="comment">
        <p>{{cmt.contenue}}</p>
        <p class="author">Comment by: Jane Doe</p>
        <button type="button" class="btn btn-primary btn-sm"(click)="delete(cmt)" >effacer</button>
        <button *ngIf="currentUser==cmt.user.id" type="button" class="btn btn-primary btn-sm"(click)="edit(item.id)" >modifier</button>
      </div>
      <!-- Additional comments can be added here -->
    </div>

      <form [formGroup]="repform">
        <div class="form-group"  >
          <label for="comment">Comment:</label>
          <textarea class="form-control" rows="5" id="comment" formControlName="contenue"></textarea>
        </div>
        <div class="col-6 text-right"> <!-- This column takes up half of the container's width and aligns its content to the left -->
          <button type="button" class="btn btn-primary btn-sm"(click)="ajouterreponse(item.id)" >repondre</button>
        </div>
      </form>
  </div>
